mapper.select:
	select *
	from TB_PRJ_MST
   where DEL_YN != 'Y'


   	 and (TIM_NUM is null or TIM_NUM = #{timNum})


   	 and PRJ_NUM = #{prjNum}
   	 and PRJ_NM = #{prjNm}
   	 and PRJ_DIV_CD = #{prjDivCd}
   	 and PRJ_STS_CD = #{prjStsCd}
   	 and PRJ_NOM = #{prjNom}
   	 and PRJ_PLC_NM = #{prjPlcNm}
   	 and PRJ_REF_CMT = #{prjRefCmt}
   	 and TIM_NUM = #{timNum}
   	 and DSC_PER = #{dscPer}
   	 and EST_AMT = #{estAmt}
   	 and PMT_MTD_CD = #{pmtMtdCd}

   	and PRJ_START_YM >= #{prjStartYm}
   	and PRJ_END_YM < (cast(#{prjEndYm} as unsigned) + 1)

   	and CRT_DTM >= STR_TO_DATE(#{startCrtDtm},'%Y%m%d %H%i%S')
   	and CRT_DTM < STR_TO_DATE(#{endCrtDtm}+1,'%Y%m%d %H%i%S')
   	 and CRT_USR_NUM = #{crtUsrNum}

   	and CRT_DTM >= STR_TO_DATE(#{startMdfDtm},'%Y%m%d %H%i%S')
   	and CRT_DTM < STR_TO_DATE(#{endMdfDtm}+1,'%Y%m%d %H%i%S')
   	 and MDF_USR_NUM = #{mdfUsrNum}
   order by CRT_DTM

mapper.insert:

	      SELECT FN_GET_PRJ_NUM()

 		INSERT INTO TB_PRJ_MST
 		(
 			PRJ_NUM
		  , PRJ_NM
		  , PRJ_DIV_CD
		  , PRJ_STS_CD
		  , PRJ_NOM
		  , PRJ_PLC_NM
		  , PRJ_REF_CMT
		  , TIM_NUM
		  , DSC_PER
		  , EST_AMT
		  , PMT_MTD_CD
		  , DEL_YN
		  , CRT_DTM
		  , CRT_USR_NUM
		  , MDF_DTM
		  , MDF_USR_NUM
		  , PRJ_START_YM
		  , PRJ_END_YM
 		)
 		VALUES
 		(
 			#{prjNum}
		  , #{prjNm}
		  , #{prjDivCd}
		  , #{prjStsCd}
		  , #{prjNom}
		  , #{prjPlcNm}
		  , #{prjRefCmt}
		  , #{timNum}
		  , #{dscPer}
		  , #{estAmt}
		  , #{pmtMtdCd}
		  , #{delYn}
		  , SYSDATE()
		  , #{crtUsrNum}
		  , SYSDATE()
		  , #{mdfUsrNum}
		  , #{prjStartYm}
		  , #{prjEndYm}
 		)
