mapper.insert:
INSERT INTO TB_USR_MST
(
	USR_NUM,
	USR_NAME,
	USR_BIR_MD,
	USR_EMAIL,
	USR_TEL_NUM,
	INC_CD,
	APO_CD,
	TIM_NUM,
	JOIN_YN,
	DEL_YN,
	CRT_DTM,
	CRT_USR_NUM
) values (
	(SELECT FN_GET_USR_NUM())
	, #{usrName}
	, #{usrBirMd}
	, #{usrEmail}
	, #{usrTelNum}
	, #{incCd}
	, #{apoCd}
	, #{timNum}
	, 'N'
	, 'N'
	, SYSDATE()
	, #{masterNum}
)

mapper.select:
SELECT count(*)
  FROM TB_USR_MST 
 WHERE USR_NAME = #{usrName}
   AND USR_BIR_MD = #{usrBirth}
